import React, { Component }from "react";
import SchoolDataService from "../service/VeiculoDataService";

class ListVeiculosComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            veiculos: [],
            message: null
        }
        this.deleteVeiculoClicked = this.deleteVeiculoClicked.bind(this)
        this.updateVeiculoClicked = this.updateVeiculoClicked.bind(this)
        this.addVeiculoClicked = this.addVeiculoClicked.bind(this)
        this.refreshVeiculos = this.refreshVeiculos.bind(this)
    }

    componentDidMount() {
        this.refreshVeiculos();
    }

    refreshVeiculos() {
        SchoolDataService.retrieveAllVeiculos()
            .then(
                response => {
                    this.setState({veiculos: response.data})
                }
            )
    }

    addVeiculoClicked() {
        this.props.history.push(`/veiculos/create`)
    }

    updateVeiculoClicked(id) {
        console.log('update ' + id)
        this.props.history.push(`/veiculos/${id}`)
    }

    deleteVeiculoClicked(id) {
        SchoolDataService.deleteVeiculo(id)
            .then(
                response => {
                    this.setState({ message: `Veículo ${id} removido com sucesso.` })
                    this.refreshVeiculos()
                }
            )
    }

    render() {
        return (
            <>
                <div className="container">
                    <h3>List Veiculos</h3>
                    {this.state.message && <div className="alert alert-success">{this.state.message}</div>}
                    <div className="container">
                        <table className="table">
                            <thead>
                            <tr>
                                <th>Id</th>
                                <th>Descrição</th>
                                <th>Ações</th>
                            </tr>
                            </thead>
                            <tbody>
                            {
                                this.state.veiculos.map(
                                    veiculo =>
                                        <tr key={veiculo.id}>
                                            <td>{veiculo.id}</td>
                                            <td>{veiculo.descricao}</td>
                                            <td>
                                                <button className="btn btn-success" onClick={() => this.updateVeiculoClicked(veiculo.id)}>Update</button>
                                                <button className="btn btn-warning" onClick={() => this.deleteVeiculoClicked(veiculo.id)}>Delete</button>
                                            </td>
                                        </tr>
                                )
                            }
                            </tbody>
                        </table>
                        <div className="row">
                            <button className="btn btn-success" onClick={this.addVeiculoClicked}>Add</button>
                        </div>
                    </div>
                </div>
            </>
        )
    }
}
export default ListVeiculosComponent
